mapper.insert:
INSERT INTO 
	user_info(user_id, user_pwd, user_name, user_email)
VALUES(
	#{userId}
	,#{userPwd}
	,#{userName}
	,#{userEmail}
)

mapper.update:
UPDATE
	user_info
SET
	user_image = #{userImage}
WHERE
	user_id = #{userId}

mapper.delete:
DELETE FROM
	user_follow
WHERE
	user_id = #{userId}
AND
	other_id = #{otherId}
